define('text!../WEB-INF/tags/widget/spec/layout.tag',[],function () { return '<%--\n  レイアウト定義\n  @author Iwauo Tajima\n  @from 1.4\n--%>\n<%@ tag pageEncoding="UTF-8" description="画面設計書での画面内表示区分(レイアウト)を定義する。" %>\n<%@ taglib prefix="n"      uri="http://tis.co.jp/nablarch" %>\n<%---------------------- 属性定義 ----------------------%>\n<%@ attribute name="name" description="レイアウト名[設計書表示必須]" %>\n\n<jsp:doBody />\n';});


