define('text!../WEB-INF/tags/widget/spec/validation.tag',[],function () { return '<%--\n  項目間・セマンティク精査定義\n  @author Iwauo Tajima\n  @from 1.4\n--%>\n<%@ tag pageEncoding="UTF-8" description="項目間・セマンティク精査定義" %>\n<%@ taglib prefix="n"      uri="http://tis.co.jp/nablarch" %>\n<%---------------------- 属性定義 ----------------------%>\n<%@ attribute name="name" description="精査処理名" %>\n<%@ attribute name="target" description="対象項目名" %>\n<%@ attribute name="condition" description="精査が成功する条件" %>\n<%@ attribute name="messageId" description="精査エラー時に表示するメッセージID" %>\n<%@ attribute name="messageParam" description="メッセージ内で使用する埋め込みパラメータの内容。「|」区切りで複数の埋め込みパラメータを指定する。" %>\n\n';});


define('text!../WEB-INF/tags/widget/tab/content.tag',[],function () { return '<%--\n  タブによる表示内容切り替え\n  @author Iwauo Tajima\n--%>\n\n<%@ tag pageEncoding="UTF-8" description="タブによる表示内容切り替え" %>\n<%@ taglib prefix="n" uri="http://tis.co.jp/nablarch" %>\n<%@ taglib prefix="c" uri="http://java.sun.com/jsp/jstl/core" %>\n\n<%@ attribute name="title"    description="タブに表示する見出し" required="true" rtexprvalue="true" %>\n<%@ attribute name="value"   description="タブの識別名" required="true" rtexprvalue="true" %>\n<%@ attribute name="selected" description="タブの初期選択状態" rtexprvalue="true" %>\n<%@ attribute name="cssClass" description="HTMLのclass属性値" rtexprvalue="true" %>\n\n<%-- 読み取り専用を表すcssクラスの定義 --%>\n<n:set var="tabContentId" value="tab_content_${value}_content" />\n<n:set var="isSelected"   value="${(selected) ? \'selected\' : \'\'}" />\n\n<%-- タブ --%>\n<c:if test="${renderingTabContent == \'false\'}">\n<div class="<n:write name=\'cssClass\' withHtmlFormat=\'false\' />\n            nablarch_Tab\n              -content  #<n:write name=\'tabContentId\' withHtmlFormat=\'false\' />\n              -name     <n:write name=\'tabGroupName\' withHtmlFormat=\'false\' />\n              -value    <n:write name=\'value\' withHtmlFormat=\'false\' />\n              <n:write name=\'isSelected\' withHtmlFormat=\'false\' />">\n  <n:prettyPrint name="title" />\n</div>\n</c:if>\n\n<%-- コンテンツ --%>\n<c:if test="${renderingTabContent == \'true\'}">\n<div id="<n:write name=\'tabContentId\' withHtmlFormat=\'false\' />" style="display:none;">\n  <jsp:doBody/>\n</div>\n</c:if>\n';});


define('text!../WEB-INF/tags/widget/tab/group.tag',[],function () { return '<%--\n  タブによる表示内容切り替え\n  @author Iwauo Tajima\n--%>\n\n<%@ tag pageEncoding="UTF-8" description="タブによる表示内容切り替え" %>\n<%@ taglib prefix="n" uri="http://tis.co.jp/nablarch" %>\n\n<%@ attribute name="name" description="タブグループ名(必須)" required="true" rtexprvalue="true" %>\n\n<%-- タブ --%>\n<div class="tabgroup">\n  <n:set var="tabGroupName" value="${name}" />\n  <n:set var="renderingTabContent" value="false" />\n  <jsp:doBody/>\n</div>\n\n<%-- タブの選択状態保持用 --%>\n<n:plainHidden name="${tabGroupName}" />\n\n<%-- コンテンツ --%>\n<n:set var="renderingTabContent" value="true" />\n<jsp:doBody/>\n';});


define('text!../WEB-INF/tags/widget/tab/link.tag',[],function () { return '<%--\n  タブ型リンク\n  @author Iwauo Tajima\n--%>\n\n<%@ tag pageEncoding="UTF-8" description="タブ型リンク" %>\n<%@ taglib prefix="n" uri="http://tis.co.jp/nablarch" %>\n<%@ taglib prefix="c" uri="http://java.sun.com/jsp/jstl/core" %>\n\n<%@ attribute name="title" description="タブに表示する見出し" rtexprvalue="true" required="true" %>\n<%@ attribute name="uri" description="遷移先URI" required="true" rtexprvalue="true" %>\n<%@ attribute name="id" description="HTMLのid属性" rtexprvalue="true" %>\n<%@ attribute name="cssClass" description="HTMLのclass属性" rtexprvalue="true" %>\n<%@ attribute name="dummyUri" rtexprvalue="true" description="画面設計時に使用する遷移先（紙芝居のように画面遷移する）（本番環境では使用しない）" %>\n<%@ attribute name="allowDoubleSubmission" description="2重サブミットを許可するか否か。falseを指定した場合は許可しない。デフォルトはtrue" rtexprvalue="true" %>\n\n<%-- タブ --%>\n<c:if test="${renderingTabContent == \'false\'}">\n<div class="nablarch_TabLink">\n<n:submitLink\n    id="${id}"\n    uri="${uri}"\n    displayMethod="NORMAL"\n    cssClass="TabLink -name ${tabGroupName} ${cssClass}"\n    allowDoubleSubmission="${(empty allowDoubleSubmission) ? true : allowDoubleSubmission}">\n  <n:prettyPrint name="title" />\n  <jsp:doBody />\n</n:submitLink>\n</div>\n</c:if>\n';});


define('text!../WEB-INF/tags/widget/table/plain.tag',[],function () { return '<%--\n  テーブル表示（ページングや件数の表示を行わない。）\n  @author Iwauo Tajima\n--%>\n<%@ tag pageEncoding="UTF-8" description="ページングや件数の表示を行わないテーブルを出力するウィジェット" %>\n<%@ taglib prefix="n" uri="http://tis.co.jp/nablarch" %>\n<%@ taglib prefix="c" uri="http://java.sun.com/jsp/jstl/core" %>\n<%@ taglib prefix="fn" uri="http://java.sun.com/jsp/jstl/functions" %>\n<%@ taglib prefix="listSearchResult" tagdir="/WEB-INF/tags/listSearchResult" %>\n\n<%-- ============================ 属性定義 =============================== --%>\n\n<%@ attribute name="title"          description="テーブルタイトル[ローカル表示必須]" required="true" rtexprvalue="true" %>\n<%@ attribute name="id"             description="テーブルのID属性" rtexprvalue="true" %>\n<%@ attribute name="showTitle"      description="タイトルを見出しとして表示するか(デフォルト:true)" rtexprvalue="true" %>\n<%@ attribute name="resultSetName"  description="検索結果を格納する変数名(resultNumNameといずれか必須)" rtexprvalue="true" %>\n<%@ attribute name="resultNumName"  description="検索結果件数を格納する変数名(resultSetNameといずれか必須)" rtexprvalue="true" %>\n<%@ attribute name="resultSetCss"   description="検索結果表示領域に適用するCSSクラス" rtexprvalue="true" %>\n<%@ attribute name="sampleResults"  description="サンプルで表示する件数(本番動作時では使用されない。)[ローカル表示必須]" rtexprvalue="true" %>\n\n<%-- ================================================================================ --%>\n\n\n<%-- ============================ 属性定義（設計書向け） =============================== --%>\n\n<%@ attribute name="comment"                   description="このテーブルについての備考（画面概要の一覧表示のリストで、備考に表示される）" %>\n<%@ attribute name="estimatedMaxSearchResults" description="検索結果として想定される最大件数（画面概要の一覧表示のリストで、想定検索最大件数に表示される）" %>\n\n<%-- ================================================================================ --%>\n\n\n<%-- 複数行表示対応 --%>\n<%@ attribute name="multipleRowLayout" description="複数の行レイアウトが含まれる場合かどうか。デフォルトは\'false\'" rtexprvalue="true" %>\n\n<c:if test="${empty showTitle}">\n  <n:set var="showTitle" value="true" />\n</c:if>\n\n<c:if test="${not empty resultSetName}">\n  <n:set var="resultSet" name="${resultSetName}" scope="page" bySingleValue="false" />\n</c:if>\n\n<c:if test="${not empty resultNumName}">\n  <n:set var="resultNum" name="${resultNumName}" scope="page" bySingleValue="false" />\n  <n:hidden name="${resultNumName}" />\n</c:if>\n\n<n:set var="tableId" value="${(not empty id) ? id : resultSetName}" />\n<c:if test="${not empty tableId}">\n  <n:set var="tableId" value="${fn:replace(tableId, \'.\', \'_\')}"/>\n</c:if>\n\n<c:if test="${(not empty resultSet) || (not empty resultNum)}">\n  <c:if test="${showTitle == \'true\'}">\n  <div class="title">\n    <h2>\n      <n:prettyPrint name="title" />\n    </h2>\n  </div>\n  </c:if>\n</c:if>\n\n<listSearchResult:listSearchResult\n  resultSetName      = "${resultSetName}"\n  resultNumName      = "${resultNumName}"\n  useResultCount     = "false"\n  usePaging          = "false"\n  resultSetCss       = "${resultSetCss} nablarch_resultSet"\n  id                 = "${id}">\n\n  <jsp:attribute name="headerRowFragment">\n    <%-- ヘッダー部 --%>\n    <c:if test="${multipleRowLayout != \'true\'}">\n    <tr class="<n:write name=\'oddEvenCss\' withHtmlFormat=\'false\' />">\n      <n:set var="renderingHeader" value="true"  />\n      <n:set var="renderingRows"   value="false" />\n      <n:set var="renderingInlay"  value="false"  />\n      <jsp:doBody/>\n      <th class="nablarch_AdditionalColumnInlayToggle"></th>\n    </tr>\n    </c:if>\n    <c:if test="${multipleRowLayout == \'true\'}">\n      <n:set var="renderingHeader" value="true" />\n      <n:set var="renderingRows"   value="false" />\n      <n:set var="renderingInlay"  value="false"  />\n      <jsp:doBody/>\n    </c:if>\n  </jsp:attribute>\n\n  <jsp:attribute name="bodyRowFragment">\n    <%-- ボディ部 --%>\n    <c:if test="${multipleRowLayout != \'true\'}">\n    <tr class="<n:write name=\'oddEvenCss\' withHtmlFormat=\'false\' />">\n      <n:set var="renderingHeader" value="false" />\n      <n:set var="renderingRows"   value="true" />\n      <n:set var="renderingInlay"  value="false"  />\n      <jsp:doBody/>\n      <td class="nablarch_AdditionalColumnInlayToggle">\n        <n:a\n          href="#"\n          cssClass="nablarch_Collapsible\n                   -content \'#inlay_${tableId}_${count}\'\n                   -value   \'inlay_${tableId}_${count}\'\n                   -name    \'inlay_open_status\'\n                   -closed">\n        </n:a>\n      </td>\n    </tr>\n    <%-- 付加情報項目表示域 --%>\n    <n:set var="additionalColumnInlayId" value="inlay_${tableId}_${count}"/>\n    <tr id="<n:write name=\'additionalColumnInlayId\' withHtmlFormat=\'false\'/>" class="nablarch_AdditionalColumnInlay">\n      <td colspan="100">\n        <n:set var="renderingHeader" value="false" />\n        <n:set var="renderingRows"   value="false" />\n        <n:set var="renderingInlay"  value="true"  />\n        <jsp:doBody/>\n      </td>\n    </tr>\n    </c:if>\n\n    <c:if test="${multipleRowLayout == \'true\'}">\n      <n:set var="renderingHeader" value="false" />\n      <n:set var="renderingRows"   value="true" />\n      <n:set var="renderingInlay"  value="false"  />\n      <jsp:doBody/>\n    </c:if>\n\n  </jsp:attribute>\n\n</listSearchResult:listSearchResult>\n';});


define('text!../WEB-INF/tags/widget/table/row.tag',[],function () { return '<%--\n  テーブル内の行レイアウト定義(1テーブル内に複数の行レイアウトが含まれる場合に使用する。)\n  @author Iwauo Tajima\n--%>\n<%@ tag pageEncoding="UTF-8" description="ページングや件数の表示を行わないテーブルを出力するウィジェット" %>\n<%@ taglib prefix="n" uri="http://tis.co.jp/nablarch" %>\n<%@ taglib prefix="c" uri="http://java.sun.com/jsp/jstl/core" %>\n\n<%-- ============================ 属性定義 =============================== --%>\n<%@ attribute name="cond"     description="この行レイアウトを使用する条件(未設定時は\'true:常に使用する\')" rtexprvalue="true" %>\n<%@ attribute name="cssClass" description="各カラムに指定するCSSクラス" rtexprvalue="true" %>\n\n<c:if test="${(cond == null) ? \'true\' : cond}">\n  <tr class="<n:write name=\'cssClass\' withHtmlFormat=\'false\' /> <n:write name=\'oddEvenCss\' withHtmlFormat=\'false\' />">\n    <jsp:doBody/>\n  </tr>\n</c:if>\n';});


define('text!../WEB-INF/tags/widget/table/search_result.tag',[],function () { return '<%--\n  検索結果のテーブル表示\n  @author Iwauo Tajima\n--%>\n<%@ tag pageEncoding="UTF-8" description="テーブル（検索結果など）を出力するウィジェット" %>\n<%@ taglib prefix="n" uri="http://tis.co.jp/nablarch" %>\n<%@ taglib prefix="c" uri="http://java.sun.com/jsp/jstl/core" %>\n<%@ taglib prefix="listSearchResult" tagdir="/WEB-INF/tags/listSearchResult" %>\n\n<%-- ============================ 属性定義 =============================== --%>\n\n<%@ attribute name="title"              description="見出し文字列[ローカル表示必須]" required="true" rtexprvalue="true" %>\n<%@ attribute name="id"                 description="テーブルのID属性" rtexprvalue="true" %>\n<%@ attribute name="showTitle"          description="タイトルを見出しとして表示するか(デフォルト:true)" rtexprvalue="true" %>\n<%@ attribute name="searchUri"          description="検索処理を行うリクエストのURI" rtexprvalue="true" %>\n<%@ attribute name="listSearchInfoName" description="検索条件を格納する変数名" required="true" rtexprvalue="true" %>\n<%@ attribute name="resultSetName"      description="検索結果を格納する変数名" rtexprvalue="true" %>\n<%@ attribute name="resultNumName"      description="検索結果件数を格納する変数名(resultSetNameといずれか必須)" rtexprvalue="true" %>\n<%@ attribute name="resultSetCss"       description="検索結果表示領域に適用するCSSクラス" rtexprvalue="true" %>\n<%@ attribute name="usePaging"          description="ページングを使用するか否か（デフォルトは使用する:true）" rtexprvalue="true" %>\n<%@ attribute name="sampleResults"      description="サンプルで表示する件数(本番動作時では使用されない。)[ローカル表示必須]" rtexprvalue="true" %>\n\n<%-- ================================================================================ --%>\n\n\n<%-- ============================ 属性定義（設計書向け） =============================== --%>\n\n<%@ attribute name="sortCondition"             description="テーブルの初期ソート条件（画面概要の一覧表示のリストで、ソート条件に表示される）" %>\n<%@ attribute name="comment"                   description="テーブルについての備考（画面概要の一覧表示のリストで、備考に表示される）" %>\n<%@ attribute name="estimatedMaxSearchResults" description="検索結果として想定される最大件数（画面概要の一覧表示のリストで、想定検索最大件数に表示される）" %>\n\n<%-- ================================================================================ --%>\n\n\n<%-- 複数行表示対応 --%>\n<%@ attribute name="multipleRowLayout" description="複数の行レイアウトが含まれる場合かどうか。デフォルトは\'false\'" rtexprvalue="true" %>\n\n<c:if test="${not empty searchUri}">\n  <n:set var="searchUri" value="${searchUri}" />\n</c:if>\n\n<c:if test="${not empty listSearchInfoName}">\n  <n:set var="listSearchInfoName" value="${listSearchInfoName}" />\n</c:if>\n\n<c:if test="${empty showTitle}">\n  <n:set var="showTitle" value="true" />\n</c:if>\n\n<c:if test="${not empty resultSetName}">\n  <n:set var="resultSet" name="${resultSetName}" scope="page" bySingleValue="false" />\n</c:if>\n<c:if test="${not empty resultNumName}">\n  <n:set var="resultNum" name="${resultNumName}" scope="page" bySingleValue="false" />\n  <n:hidden name="${resultNumName}" />\n</c:if>\n\n<c:if test="${(not empty resultSet) || (not empty resultNum)}">\n  <c:if test="${showTitle == \'true\'}">\n  <div class="title">\n    <h2>\n      <n:prettyPrint name="title" />\n      (<n:write name="${listSearchInfoName}.resultCount" />件)\n    </h2>\n  </div>\n  </c:if>\n</c:if>\n\n<listSearchResult:listSearchResult\n  listSearchInfoName = "${listSearchInfoName}"\n  searchUri          = "${searchUri}"\n  resultSetName      = "${resultSetName}"\n  resultNumName      = "${resultNumName}"\n  useResultCount     = "false"\n  usePaging          = "${(not empty usePaging) ? usePaging : true}"\n  pagingPosition     = "both"\n  resultSetCss       = "${resultSetCss} nablarch_resultSet"\n  id                 = "${id}">\n\n  <jsp:attribute name="headerRowFragment">\n    <%-- ヘッダー部 --%>\n    <c:if test="${multipleRowLayout != \'true\'}">\n    <tr class="<n:write name=\'oddEvenCss\' withHtmlFormat=\'false\' />">\n      <n:set var="renderingHeader" value="true"  />\n      <n:set var="renderingRows"   value="false" />\n      <jsp:doBody/>\n    </tr>\n    </c:if>\n    <c:if test="${multipleRowLayout == \'true\'}">\n      <n:set var="renderingHeader" value="true" />\n      <n:set var="renderingRows"   value="false" />      \n      <jsp:doBody/>\n    </c:if>\n  </jsp:attribute>\n  \n  <jsp:attribute name="bodyRowFragment">\n    <%-- ボディ部 --%>\n    <c:if test="${multipleRowLayout != \'true\'}">\n    <tr class="<n:write name=\'oddEvenCss\' withHtmlFormat=\'false\' />">\n      <n:set var="renderingHeader" value="false" />\n      <n:set var="renderingRows"   value="true" />\n      <jsp:doBody/>\n    </tr>\n    </c:if>\n\n    <c:if test="${multipleRowLayout == \'true\'}">\n      <n:set var="renderingHeader" value="false" />\n      <n:set var="renderingRows"   value="true" />\n      <jsp:doBody/>\n    </c:if>\n  </jsp:attribute>\n\n</listSearchResult:listSearchResult>\n';});


define('text!../WEB-INF/tags/widget/table/treelist.tag',[],function () { return '<%--\n  階層化リスト表示\n  @author Iwauo Tajima\n--%>\n<%@ tag pageEncoding="UTF-8" description="部署一覧、部品表など、階層化されたデータの一覧を表示するウィジェット" %>\n<%@ taglib prefix="n" uri="http://tis.co.jp/nablarch" %>\n<%@ taglib prefix="c" uri="http://java.sun.com/jsp/jstl/core" %>\n<%@ taglib prefix="listSearchResult" tagdir="/WEB-INF/tags/listSearchResult" %>\n\n<%-- ============================ 属性定義 =============================== --%>\n<%@ attribute name="title"              description="見出し文字列" required="true" rtexprvalue="true" %>\n<%@ attribute name="id"                 description="テーブルのID属性" rtexprvalue="true" %>\n<%@ attribute name="resultSetName"      description="検索結果を格納する変数名" required="true" rtexprvalue="true" %>\n<%@ attribute name="resultSetCss"       description="検索結果表示領域に適用するCSSクラス" rtexprvalue="true" %>\n<%@ attribute name="key"                description="階層化対象キー" required="true" rtexprvalue="true" %>\n<%@ attribute name="name"               description="ツリーの開閉状態の保持に使用するフォーム要素のname属性値" required="true" rtexprvalue="true" %>\n<%@ attribute name="hierarchy"          description="階層構造の定義式。以下の3つの書式のいずれかを指定する。 1. chars:(数値)|(数値)|...|(数値) 各要素のid属性値を先頭からの文字数で分割し階層を決定する方法。 2. separator:(区切り文字列) 各要素のid属性値を区切り文字列で分割し階層を決定する方法。 3. function: (グローバル関数名) 指定されたグローバル関数を使用して階層を決定する方法。" required="true" rtexprvalue="true" %>\n\n<%@ attribute name="sampleResults"      description="サンプルで表示する件数(本番動作時では使用されない。)" rtexprvalue="true" %>\n\n<%-- ================================================================================ --%>\n\n\n<%-- ============================ 属性定義（設計書向け） =============================== --%>\n\n<%@ attribute name="sortCondition"             description="このテーブルの初期ソート条件（画面概要の一覧表示のリストで、ソート条件に表示される）" %>\n<%@ attribute name="comment"                   description="このテーブルについての備考（画面概要の一覧表示のリストで、備考に表示される）" %>\n<%@ attribute name="estimatedMaxSearchResults" description="検索結果として想定される最大件数（画面概要の一覧表示のリストで、想定検索最大件数に表示される）" %>\n\n<%-- ================================================================================ --%>\n\n\n<n:set var="resultSet" name="${resultSetName}" scope="page" bySingleValue="false" />\n\n<c:if test="${resultSet != null}">\n  <div class="title">\n    <h2><n:prettyPrint name="title" /></h2>\n  </div>\n</c:if>\n\n<%-- 各ブロック、ツリーの開閉状態 --%>\n<n:plainHidden name="${name}"></n:plainHidden>\n\n<listSearchResult:listSearchResult\n  resultSetName       = "${resultSetName}"\n  useResultCount      = "false"\n  usePaging           = "false"\n  resultSetCss        = "${resultSetCss}\n                         nablarch_resultSet\n                         nablarch_TreeList\n                           -name ${name}\n                           -hierarchy ${hierarchy}\n                           -depth     3\n                           -items      tr:has(td)\n                           -navigation td.tree_toggler">\n  <jsp:attribute name="headerRowFragment">\n    <%-- ヘッダー部 --%>\n    <tr class="<n:write name=\'oddEvenCss\' withHtmlFormat=\'false\' />">\n      <n:set var="renderingHeader" value="true"  />\n      <n:set var="renderingRows"   value="false" />\n      <n:set var="columnNum"       value="0" />\n      <jsp:doBody/>\n    </tr>\n  </jsp:attribute>\n    \n        \n  <jsp:attribute name="bodyRowFragment">\n    <%-- ボディ部 --%>\n    <n:set var="row_key" name="row.${key}" />  \n    <tr id="<n:write name=\'row_key\' withHtmlFormat=\'false\' />"\n        class="<n:write name=\'oddEvenCss\' withHtmlFormat=\'false\' />">\n      <n:set var="renderingHeader" value="false" />\n      <n:set var="renderingRows"   value="true" />\n      <jsp:doBody/>\n    </tr>\n  </jsp:attribute>\n\n</listSearchResult:listSearchResult>\n';});


